import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { Button, Form, Modal } from "react-bootstrap";
import { AuthContext } from "../context/Auth.context";

function ArtPostDetails() {
  const { artpostId } = useParams();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5005";
  const [artpost, setArtPost] = useState(null);
  const [showUpdateForm, setShowUpdateForm] = useState(false);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);

  useEffect(() => {
    axios
      .get(`${API_URL}/api/posts/artposts/${artpostId}`)
      .then((response) => {
        console.log(response.data);
        setArtPost(response.data);
      })
      .catch((error) => console.log(error));
  }, [artpostId]);

  const handleUpdateSubmit = (e) => {
    e.preventDefault();
    // Add logic to handle the update form submission
  };

  const handleDelete = () => {
    // Add logic to handle the delete operation
  };

  return (
    <div>
      {artpost ? (
        <div>
          <h2>Art Details:</h2>
          <div>
            <img src={artpost.art_image} alt={artpost.title} />
            <h3>Title: {artpost.title}</h3>
            <h4>Artist: {artpost.artist}</h4>
            {artpost.description && <p>Description: {artpost.description}</p>}
            {artpost.medium && <p>Medium: {artpost.medium}</p>}
            {artpost.year && <p>Year: {artpost.year}</p>}
            {artpost.dimensions && <p>Dimensions: {artpost.dimensions}</p>}
          </div>
          {user?._id === artpost.author && (
            <>
              <Button
                variant="warning"
                onClick={() => setShowUpdateForm(!showUpdateForm)}
              >
                {showUpdateForm ? "Hide Form" : "Update"}
              </Button>
              <Button
                variant="danger"
                onClick={() => setShowDeleteConfirmation(!showDeleteConfirmation)}
              >
                Delete
              </Button>
            </>
          )}
          {showUpdateForm && (
            <Form onSubmit={handleUpdateSubmit}>
              {/* Add the form fields here */}
              <Button variant="primary" type="submit">
                Update Art Post
              </Button>
            </Form>
          )}
          <Modal
            show={showDeleteConfirmation}
            onHide={() => setShowDeleteConfirmation(false)}
          >
            <Modal.Header closeButton>
              <Modal.Title>Delete Art Post</Modal.Title>
            </Modal.Header>
            <Modal.Body>Are you sure you want to delete this art post?</Modal.Body>
            <Modal.Footer>
              <Button
                variant="secondary"
                onClick={() => setShowDeleteConfirmation(false)}
              >
                Close
              </Button>
              <Button variant="danger" onClick={handleDelete}>
                Delete
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      ) : (
        <p>Loading art details...</p>
      )}
    </div>
  );
}

export default ArtPostDetails;
