import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { Button, Form, Modal } from "react-bootstrap";
import { AuthContext } from "../context/Auth.context";

function ArtPostDetails() {
  const { artpostId } = useParams();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5005";
  const [artpost, setArtPost] = useState(null);
  const [showUpdateForm, setShowUpdateForm] = useState(false);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);

  useEffect(() => {
    axios
      .get(`${API_URL}/api/posts/artposts/${artpostId}`)
      .then((response) => {
        console.log(response.data);
        setArtPost(response.data);
      })
      .catch((error) => console.log(error));
  }, [artpostId]);

  const handleUpdateSubmit = (e) => {
    e.preventDefault();
    // Add logic to handle the update form submission
  };

  const handleDelete = () => {
    // Add logic to handle the delete operation
  };

  return (
    <div>
      {artpost ? (
        <div>
          <h2>Art Details:</h2>
          <div>
            <img src={artpost.art_image} alt={artpost.title} />
            <h3>Title: {artpost.title}</h3>
            <h4>Artist: {artpost.artist}</h4>
            {artpost.description && <p>Description: {artpost.description}</p>}
            {artpost.medium && <p>Medium: {artpost.medium}</p>}
            {artpost.year && <p>Year: {artpost.year}</p>}
            {artpost.dimensions && <p>Dimensions: {artpost.dimensions}</p>}
          </div>
          {user?._id === artpost.author && (
            <>
              <Button
                variant="warning"
                onClick={() => setShowUpdateForm(!showUpdateForm)}
              >
                {showUpdateForm ? "Hide Form" : "Update"}
              </Button>
              <Button
                variant="danger"
                onClick={() => setShowDeleteConfirmation(!showDeleteConfirmation)}
              >
                Delete
              </Button>
            </>
          )}
          {showUpdateForm && (
  <Form onSubmit={handleUpdateSubmit}>
    <Form.Group controlId="formArtist">
      <Form.Label>Artist:</Form.Label>
      <Form.Control type="text" value={artist} onChange={(e) => setArtist(e.target.value)} />
    </Form.Group>

    <Form.Group controlId="formTitle">
      <Form.Label>Title:</Form.Label>
      <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
    </Form.Group>

    <Form.Group controlId="formDescription">
      <Form.Label>Description:</Form.Label>
      <Form.Control type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
    </Form.Group>

    <Form.Group controlId="formMedium">
      <Form.Label>Medium:</Form.Label>
      <Form.Control type="text" value={medium} onChange={(e) => setMedium(e.target.value)} />
    </Form.Group>

    <Form.Group controlId="formYear">
      <Form.Label>Year:</Form.Label>
      <Form.Control type="number" value={year} onChange={(e) => setYear(e.target.value)} />
    </Form.Group>

    <Form.Group controlId="formDimensions">
      <Form.Label>Dimensions:</Form.Label>
      <Form.Control type="text" value={dimensions} onChange={(e) => setDimensions(e.target.value)} />
    </Form.Group>

    <Form.Group controlId="formArtImage">
      <Form.Label>Art Image URL:</Form.Label>
      <Form.Control type="text" value={art_image} onChange={(e) => setArtImage(e.target.value)} />
    </Form.Group>

    <Button variant="primary" type="submit">
      Update
    </Button>
  </Form>
)}
          <Modal
            show={showDeleteConfirmation}
            onHide={() => setShowDeleteConfirmation(false)}
          >
            <Modal.Header closeButton>
              <Modal.Title>Delete Art Post</Modal.Title>
            </Modal.Header>
            <Modal.Body>Are you sure you want to delete this art post?</Modal.Body>
            <Modal.Footer>
              <Button
                variant="secondary"
                onClick={() => setShowDeleteConfirmation(false)}
              >
                Close
              </Button>
              <Button variant="danger" onClick={handleDelete}>
                Delete
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      ) : (
        <p>Loading art details...</p>
      )}
    </div>
  );
}

export default ArtPostDetails;
