import React, { useState, useEffect, useContext } from 'react'
import axios from "axios"


import { useParams, Link } from 'react-router-dom'
import { AuthContext } from '../context/Auth.context'

const API_URL = "http://localhost:5005/api/home"


function UserPage(props) {

    const {user} = useContext(AuthContext)
    console.log( "user id", user?._id)

    const userId = user?._id

   /*  const {userId} = useParams()
    console.log("userId is: ", userId)
    console.log("useParams() is: ", useParams()) */

    const [posts, setPosts] = useState([]);
   const [artPosts, setArtPosts] = useState([]);

    /* const getUser = replaceThis.find((user) => {
        return User._id === userId
    }) */

    function getAllPosts() {
        axios.get(API_URL)
            .then((response) => {
                console.log(response.data)
                setPosts(response.data.posts)
                setArtPosts(response.data.artPosts)
                /* setIsLoading(false); */
            })
            .catch(error => {
                console.log(error)
              /*   setIsLoading(false); */
            });
    }

    useEffect(() => {
        getAllPosts();
    }, []); 

  return (
    <div>
    <div> {/* div for user information and options to do */}
        <img src="https://static.vecteezy.com/system/resources/previews/007/033/146/original/profile-icon-login-head-icon-vector.jpg"/>
        <p>username</p>
        <p>user's fullname</p>
        <p>email {user?.email}</p>
        <p>change name?</p>
        <button>Delete Account</button>
    </div>

<div className='userPage-grid'>
    <div> {/* div for displaying posts created by user */}
        {posts.map((post) => {
            return(
                <div key={post._id}>
                    <h2>{post.content}</h2>
                    <h5>{post.place}</h5>
                    <img src={post.post_image}/>

                </div>
            )
        })}
    </div>

    <div> {/* div for displaying artposts created by user */}
        {artPosts.map((artpost) => {
            return(
                <div key={artpost._id}>

                    <h2>{artpost.title}</h2>
                    <h5>{artpost.description}</h5>
                    <img src={artpost.art_image}/>
                    <p>{artpost.medium}</p>
                    <p>{artpost.year}</p>

                </div>
            )
        })}
    </div>
</div>

    </div>
  )
}

export default UserPage
