import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import ArtPostCard from "../components/ArtpostCard";

function ArtPostDetails() {
  const { artpostId } = useParams();
  const API_URL = "http://localhost:5005";
  const [artpost, setArtPost] = useState(null);

  useEffect(() => {
    axios
      .get(`${API_URL}/api/posts/artposts/${artpostId}`)
      .then((response) => {
        console.log(response.data);
        setArtPost(response.data); // Change this line
      })
      .catch((error) => console.log(error));
  }, [artpostId]);

  return (
    <div>
      {artpost ? (
        <div>
          <h2> Art Details:</h2>
          <div>
            <ArtPostCard artpost={artpost} />
          </div>
        </div>
      ) : (
        <p>Loading art details...</p>
      )}
    </div>
  );
}

export default ArtPostDetails;
