import navbarCSS from "./navbar.module.css"
import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import reactLogo from "../images/logo192.png"
import { AuthContext } from "../context/Auth.context";

function NavBar() {
  const { isLoggedIn, logOutUser } = useContext(AuthContext);
  const [showAlert, setShowAlert] = useState(false);

  const handleLogOut = () => {
    logOutUser();
    setShowAlert(true);
  }

  return (
    <div className={navbarCSS.navbar}>
      <img src={reactLogo} alt="React Logo" />

      <h3 className={navbarCSS.h3}>Macartsy</h3>

      {showAlert && (
        <div className="alert alert-info" role="alert">
          A simple info alert with <a href="#" className="alert-link">an example link</a>. Give it a click if you like.
        </div>
      )}

      <div className={navbarCSS.buttons}>
        <Link to="/about">
          <button className="btn btn-primary">About Page</button>
        </Link>
        {isLoggedIn ? (
          <>
            <Link to="/home">
              <button>Home</button>
            </Link>
            <Link to="/new-post">
              <button>Create new Post</button>
            </Link>
            <Link to="/">
              <button className="btn btn-primary" onClick={handleLogOut}>Log Out</button>
            </Link>
          </>
        ) : (
          <>
            <Link to="/signup">
              <button>Sign Up</button>
            </Link>
            <Link to={"/login"}>
              <button>Login</button>
            </Link>
          </>
        )}
      </div>
    </div>
  )
}

export default NavBar;
