import React, { useState, useEffect } from 'react';
import axios from "axios"
import { Alert } from 'react-bootstrap';
const API_URL = "http://localhost:5005"

const AuthContext = React.createContext()


function AuthProviderWrapper(props){
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [user, setUser] = useState(null);

    function authenticateUser(){
        const storedToken = localStorage.getItem("authToken")

        if (storedToken){
            axios.get(`${API_URL}/auth/verify`, {headers: {Authorization: `Bearer ${storedToken}`}})
                .then((response) => {
                    const user = response.data
                    setIsLoggedIn(true);
                    setIsLoading(false);
                    setUser(user); 
                })
                .catch((error) => {
                    setIsLoggedIn(false);
                    setIsLoading(false);
                    setUser(null); 
                })
        } else {
            setIsLoggedIn(false);
            setIsLoading(false);
            setUser(null);   
        }
    }

    function removeToken(){
        localStorage.removeItem("authToken")
    }

    function alertLogOut(){
        {['secondary', 'dark'].map((variant) => (
            <Alert key={variant} variant={variant}>
              you just logged out. <Alert.Link href="#">Log in</Alert.Link> to see what we have for you
            </Alert>
          ))}
    }

    function logOutUser(){
        removeToken()
        authenticateUser()


    }

    useEffect(() => {
        authenticateUser()
    },[])

    return (
        <AuthContext.Provider value={{isLoggedIn, isLoading, user, authenticateUser, logOutUser}}>
            {props.children}
        </AuthContext.Provider>
    )
}

export {AuthProviderWrapper, AuthContext}
