import React, { useState, useEffect } from 'react';
import axios from "axios";
import { Button, Card } from "react-bootstrap";
import { Link, useNavigate, useLocation } from "react-router-dom";

import EmptyLike from "../images/like-empty.png"
import FullLike from "../images/like-full.png"

function PostCard({ content, place, post_image, postId, getAllPosts }) {

  const [userId, setUserId] = useState()
  const [elementId, setElementId] = useState(postId)
  const [elementType, setElemenType] = useState("post")
  const [isLiked, setIsLiked] = useState(false)

  const API_URL = "http://localhost:5005";
  const navigate = useNavigate();
  const {pathname} = useLocation()

  useEffect(() => {
    // Fetch the current user id and check if the post is already liked by the user
    axios.get(`${API_URL}/api/user`)
      .then(response => {
        setUserId(response.data.id)
        axios.get(`${API_URL}/api/post/${postId}/isliked`)
          .then(response => setIsLiked(response.data.isLiked))
          .catch(error => console.log("Error while checking if post is liked: ", error))
      })
      .catch(error => console.log("Error while fetching user id: ", error))
  }, [postId])

  function handleLike(e){
    e.preventDefault()
    setIsLiked(true) // Set the isLiked state to true immediately to provide instant feedback to the user
    const requestBody = {userId, elementType, elementId} 

    axios.post(`${API_URL}/api/post/like`, requestBody)
      .then(response => console.log("Post liked successfully"))
      .catch(error => console.log("Error while liking post: ", error))
  }

  const handleDelete = () => {
    console.log(`Deleting post with id ${postId}`);

    axios
      .delete(`${API_URL}/api/posts/${postId}`)
      .then((response) => {
        console.log("Post deleted successfully:", response.data);
        getAllPosts()
        navigate("/home"); // Navigate to the home page after successful deletion
      })
      .catch((error) => {
        console.log("Error deleting post:", error);
      });
  };

  return (
    <div>
        <div className='post_card'>
          <div>
            <img className='post-image' src={post_image} alt={"photo from the event"}/>
          </div>

          <div>
            <h5>{content}</h5>
            <p>{place}</p>
          </div>

      {pathname === "/home" && 
      <Link to={`/post/${postId}`} className='post_card_link'>
      <Button>Details</Button>
      </Link>
      }
      <button 
          style={{background: "transparent", border: "none"}}
          className='artpost-like'
          onClick={handleLike}
          >
        <img 
          style={{width:"50px"}}
          defaultValue={isLiked}
          src={!isLiked ? EmptyLike : FullLike}
          />
      </button>
        </div>
    </div>
  );
}

export default PostCard;
