import React, { useState, useEffect } from 'react';
import axios from "axios";
import { Button, Card } from "react-bootstrap";
import { Link, useNavigate, useLocation } from "react-router-dom";

import EmptyLike from "../images/like-empty.png"
import FullLike from "../images/like-full.png"

function PostCard({ content, place, post_image, postId, getAllPosts }) {

  const [isLiked, setIsLiked] = useState(localStorage.getItem("isliked") === "true")

  const API_URL = "http://localhost:5005";
  const navigate = useNavigate();
  const {pathname} = useLocation()

  function handleLike(){
    if (isLiked===false) {
      setIsLiked(true)
      localStorage.setItem("isLiked", "true")
    } else {
      setIsLiked(false)
      localStorage.removeItem("isLiked")
    }
  }

  const handleDelete = () => {
    console.log(`Deleting post with id ${postId}`);

    axios
      .delete(`${API_URL}/api/posts/${postId}`)
      .then((response) => {
        console.log("Post deleted successfully:", response.data);
        getAllPosts()
        navigate("/home"); // Navigate to the home page after successful deletion
      })
      .catch((error) => {
        console.log("Error deleting post:", error);
      });
  };

  return (
    <div>
        <div className='post_card'>
          <div>
            <img className='post-image' src={post_image} alt={"photo from the event"}/>
          </div>

          <div>
            <h5>{content}</h5>
            <p>{place}</p>
          </div>

      {pathname === "/home" && 
      <Link to={`/post/${postId}`} className='post_card_link'>
      <Button>Details</Button>
      </Link>
      }
      <img 
          style={{width:"50px"}}
          className='artpost-like'
          onClick={handleLike}
          defaultValue
          src={!isLiked ? EmptyLike : FullLike}
          />
        </div>


     {/*  <Card style={{ width: "18rem" }}>
        <Card.Img variant="top" src={post_image} />
        <Card.Body>

          <Card.Text>{content}</Card.Text>
          <Link to={`/post/${postId}`}>
            <Button variant="primary">Post Details</Button>
          </Link>
          <Link to={`/post/${postId}/update`}>
            <Button variant="warning" className="mx-2">
              Update Post
            </Button>
          </Link>
          <Button variant="danger" onClick={handleDelete}>
            Delete
          </Button>
        </Card.Body>
      </Card> */}
    </div>
  );
}

export default PostCard;
