import React, { useState, useContext } from 'react'
import axios from "axios"


import { useParams, Link } from 'react-router-dom'
import { AuthContext } from '../context/Auth.context'

const API_URL = "http://localhost:5005"


function UserPage(props) {

    const user = useContext(AuthContext)
    console.log(user)

   /*  const {userId} = useParams()
    console.log("userId is: ", userId)
    console.log("useParams() is: ", useParams()) */

    const [posts, setPosts] = useState([]);
   const [artPosts, setArtPosts] = useState([]);

    /* const getUser = replaceThis.find((user) => {
        return User._id === userId
    }) */

    function getAllPosts() {
        axios.get(API_URL)
            .then((response) => {
                console.log(response.data)
                setPosts(response.data.posts)
                setArtPosts(response.data.artPosts)
                setIsLoading(false);
            })
            .catch(error => {
                console.log(error)
                setIsLoading(false);
            });
    }

    useEffect(() => {
        getAllPosts();
    }, []); 

  return (
    <div>
    <div> {/* div for user information and options to do */}
        <p>username</p>
        <p>user's fullname</p>
        <p>email</p>
        <p>change name?</p>
        <p>delete account</p>
    </div>

    <div> {/* div for displaying posts created by user */}
        {posts.map((post) => {
            return(
                <div key={post._id}>
                    <h2>{post.content}</h2>
                </div>
            )
        })}
    </div>

    <div> {/* div for displaying artposts created by user */}
        {artPosts.map((artpost) => {
            return(
                <div key={artpost._id}>
                    <h2>{artpost.title}</h2>
                </div>
            )
        })}
    </div>

    </div>
  )
}

export default UserPage
