import React, { useState, useEffect } from 'react';
import { Routes, Route, link } from 'react-router-dom';

//components
import NavBar from './components/NavBar';

//pages
import LandingPage from './pages/langing-page/LandingPage';

/* 
to use ThemeContext in each component:
1. import {ThemeContext}
2. const {theme, toggleTheme} = useContext(ThemeContext)
3. in example of the toggle theme (dark - light) -> className={"class " + theme}
4. to toggle theme -> 
    <button className='theme-btn' onClick={toggleTheme}>
        {theme === "dark" ? "light theme" : "dark theme"}
      </button>
*/

function App() {
  return (
    <div className="App">
    <NavBar/>

    <Routes>
      {/*  */}
      <Route to="/signup" /> {/* signup page */}
      <Route to="/login" /> {/* login page */}
      <Route to="/home" element={<LandingPage/>}/> {/* index page */}
      <Route to="" /> {/* main page */}
      <Route to="" /> {/* chat page */}
    </Routes>

    </div>
  );
}

export default App;
