import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Carousel } from 'react-boot';
// import 'react-responsive-carousel/lib/styles/carousel.min.css';
import '../App.css';

const API_URL = 'http://localhost:5005/api/home';

function HomePage() {
  const [posts, setPosts] = useState([]);
  const [artPosts, setArtPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  function getAllPosts() {
    axios
      .get(API_URL)
      .then((response) => {
        console.log(response.data);
        setPosts(response.data.posts);
        setArtPosts(response.data.artPosts);
        setIsLoading(false);
      })
      .catch((error) => {
        console.log(error);
        setIsLoading(false);
      });
  }

  useEffect(() => {
    getAllPosts();
  }, []);

  return (
    <div className="home-page">
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <>
          {/* Post Card */}
          {posts.map((post) => {
            return <div>{post.content}</div>;
          })}

          {/* Artpost Card */}
          {artPosts.map((artpost) => {
            return (
              <div className="artpost-card">
                <Carousel showThumbs={false} showStatus={false} autoPlay interval={3000} infiniteLoop>
                  {artpost.art_images.map((image) => (
                    <div>
                      <img src={image} alt="Art" />
                    </div>
                  ))}
                </Carousel>
                <div className="artpost-info">
                  <h3>{artpost.title}</h3>
                </div>
              </div>
            );
          })}
        </>
      )}
    </div>
  );
}

export default HomePage;
