import React, { useState, useEffect, useContext } from 'react'
import axios from "axios"


import { useParams, Link } from 'react-router-dom'
import { AuthContext } from '../context/Auth.context'
import IsPrivate from '../components/auth/IsPrivate'
import { Button, Modal } from 'react-bootstrap'

const API_URL = "http://localhost:5005"


function UserPage(props) {

    const {userId} = useParams()

    // to check the logged in account
    const {user} = useContext(AuthContext)
    /* console.log( "user id is ", user?._id) */

    const [userInfo, setUserInfo] = useState()
    const [posts, setPosts] = useState([]);
    const [artPosts, setArtPosts] = useState([]);
    const [formUpdate, setFormUpdate] = useState(false)
    const [formDelete, setFormDelete] = useState(false)


    function getUserInfo(){
        axios.get(`${API_URL}/api/user/${userId}`)
        .then((response) => {
            setUserInfo(response.data)
            console.log("user info: ", response.data._id)
            console.log("user info: ", response.data)
        })
        .catch(error => {
            console.log(error)
        });
    }

    function getAllPosts() {
        axios.get(`${API_URL}/api/home`)
            .then((response) => {
                setPosts(response.data.posts)
                setArtPosts(response.data.artPosts)
            })
            .catch(error => {
                console.log(error)
            });
    }

    useEffect(() => {
        getAllPosts();
        getUserInfo()
    }, []); 

  return (
    <div>
        <div className='userPage-grid'>
            <div className='userPage-info'> 
                <div>

                {/*  <img src="https://static.vecteezy.com/system/resources/previews/007/033/146/original/profile-icon-login-head-icon-vector.jpg"/> */}
                    <p>@{userInfo?.username}</p>
                    <p>{userInfo?.fullname}</p>
                    <p>{userInfo?.email}</p>


                    <Button variant="info">favorite posts</Button>
                    <br></br>
                    <br></br>

{/*                     {userId === user._id && <Link to={`/user/${userId}/confirm-delete`}><Button variant="danger">Delete Account</Button></Link>} */}

                    {userId === user._id && 
                        <Button variant="danger" onClick={() => setFormDelete(!formDelete)}>
                            Delete Account
                        </Button>}

                    {userId === user._id && 
                        <Button variant="warning" onClick={() => setFormUpdate(!formUpdate)}>
                            {formUpdate ? "Hide Form" : "edit Account"}
                        </Button>}
                </div>
            <div>

            {formUpdate && 

                <div>
                    <p>this is the test </p>
                </div>
                }
            </div>

            <div>
                {formDelete && 
                    <Modal {...props} size="lg" aria-labelledby="contained-modal-title-vcenter" centered>

                        <Modal.Header closeButton><Modal.Title id="contained-modal-title-vcenter">Confirm to delete your Account</Modal.Title></Modal.Header>

                        <Modal.Body>
                            <h4>Are you sure you want to delete your account? This process is irreversible. 😱</h4>
                            <p>
                            If you delete your account, you will loose access to all your posts and artposts, and can't update them in the future even with another account. Think wisely, and twice 🫤
                            </p>
                        </Modal.Body>
                        <Modal.Footer>

                            <Button onClick={props.onHide}>Close</Button>
                        </Modal.Footer>
                    </Modal>
                }
            </div>
            </div>

            <div className='userPage-posts'> 
            <h1>Posts</h1>
                {posts.map((post) => {
                    return(
                post.author === userInfo._id &&   
                        <div key={post._id}>
                            <h2>{post.content}</h2>
                            <h5>{post.place}</h5>
                            <img src={post.post_image}/>
                            <p>@{user.username}</p>

                        </div>
                    )
                })}
            </div>

            <div className='userPage-artposts'> 
            <h1>Artposts</h1>
            {artPosts.map((artpost) => {
                    return(
                        artpost.author === userInfo._id &&
                        <div key={artpost._id}>

                            <h2>{artpost.title}</h2>
                            <h5>{artpost.description}</h5>
                            <img src={artpost.art_image}/>
                            <p>{artpost.medium}</p>
                            <p>{artpost.year}</p>

                        </div>
                    )
                })}
            </div>
        </div>

    </div>
  )
}

export default UserPage
