import React, { useState, useParams } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import NewArtpost from '../components/NewArtpost'
import NewPost from '../components/NewPost'

function NewPostPage() {

  const [postForm, setPostForm] = useState(true)

  let {type} = useParams()

  function togglePostForms(){
    setPostForm()
  }

  return (
    <div >




    {postForm 
    ? 
    <Link to="/new-post/artpost"><button>Create Artpost</button></Link> 
    :
    <Link to="/new-post/post"><button>Create Post</button></Link> 
    }
  <NewPost/>  



    </div>
  )
}

export default NewPostPage
