import React, { useState, useEffect, useContext } from 'react';
import axios from "axios";
import { Button, Card } from "react-bootstrap";
import { Link, useNavigate, useLocation } from "react-router-dom";

import EmptyLike from "../images/like-empty.png"
import FullLike from "../images/like-full.png"
import { AuthContext } from '../context/Auth.context';

function PostCard({ content, place, post_image, postId, getAllPosts }) {

  const [post, setPost] = useState(null)
  const [isLiked, setIsLiked] = useState(false)

  const API_URL = "http://localhost:5005";
  const {user} = useContext(AuthContext)
  const navigate = useNavigate();
  const {pathname} = useLocation()

  useEffect(() => {
    axios.get(`${API_URL}/api/posts/posts/${postId}`)
      .then((response) => {
        setPost(response.data)
      })
      .catch(error => console.log(error))
  }, [postId])

  function handleLike(){
    axios.post(`${API_URL}/api/posts/like/${postId}/post`, user)
      .then()
  }



  const handleDelete = () => {
    console.log(`Deleting post with id ${postId}`);

    axios
      .delete(`${API_URL}/api/posts/${postId}`)
      .then((response) => {
        console.log("Post deleted successfully:", response.data);
        getAllPosts()
        navigate("/home"); // Navigate to the home page after successful deletion
      })
      .catch((error) => {
        console.log("Error deleting post:", error);
      });
  };

  return (
    <div>
        <div className='post_card'>
          <div>
            <img className='post-image' src={post_image} alt={"photo from the event"}/>
          </div>

          <div>
            <h5>{content}</h5>
            <p>{place}</p>

          </div>

      {pathname === "/home" ? 
      <Link to={`/post/${postId}`} className='post_card_link'>
      <Button>Details</Button>
      </Link> : (
        <>
      <Button>Update</Button>
      <Button>Delete</Button>
        </>
      )
      }

      <button style={{background: "transparent", border: "none"}} className='artpost-like' >
        <img style={{width:"50px"}} defaultValue={isLiked} src={!isLiked ? EmptyLike : FullLike}/>
      </button>
        </div>
    </div>
  );
}

export default PostCard;
