import navbarCSS from "./navbar.module.css"
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Link} from 'react-router-dom';
import reactLogo from "../images/logo192.png"
import { AuthContext } from "../context/Auth.context"; // Import AuthContext


function NavBar() {
  const { isLoggedIn, setIsLoggedIn, isLoading } = useContext(AuthContext); // Get properties from AuthContext

  const handleLogOut = () => {
    axios({
      method: 'post',
      url: '/api/logout',
      withCredentials: true,
    })
    .then((response ) => {
      if(response.status === 200) {
        setIsLoggedIn(false)
      }else{
        console.log("Logout Failed Please Try Again ")
      }
    })
    .catch((error ) => {
      console.log('Logout request failed:', error)
    })
  }

  return (
    <div className={navbarCSS.navbar}>
      <img src={reactLogo} alt="React Logo" />

      <h3 className={navbarCSS.h3}>Macartsy</h3>

      <div className={navbarCSS.buttons}>
        <Link to="/about">
          <button>About Page</button>
        </Link>
        <Link to="/home">
          <button>Home</button>
        </Link>
        {isLoggedIn ? (
          <>
            <Link to="/new-post">
              <button>Create new Post</button>
            </Link>
            <Link to="/">
              <button onClick={(handleLogOut) => setIsLoggedIn(false)}>Log Out</button>
            </Link>
          </>
        ) : (
          <>
            <Link to="/signup">
              <button>Sign Up</button>
            </Link>
            <Link to={"/login"}>
              <button>Login</button>
              </Link>
          </>
        )}
        {/* <select>
          <option><Link to={"/UserId"}><button>User Profile</button></Link></option>
          <option><button>change the theme</button></option>
          <option><Link to={"/UserId"}><button>Log Out</button></Link></option>
        </select> */}
      </div>
    </div>
  )
}

export default NavBar
